$("#role-button").click((function(){var o=$(this).data("user").split(",");$("#role-dialog").dialog({modal:!0,buttons:{Save:function(){var e=$("#role-select").val();e!==o[1]&&$.ajax({url:"/changeUserRole",type:"POST",data:{userId:o[0],role:e},success:function(o){console.log("Роль успешно изменена")},error:function(o){console.error("Ошибка при изменении роли: ",o)}}),$(this).dialog("close")},Cancel:function(){$(this).dialog("close")}}})}));